$().ready(function(){
	$('#goMore').click(function(){
		$('#nav').toggle();
	});
	
	$('#navHomeMenu').click(function(){
		window.location.href = '/m?uid='+uid;
	});
	
	$('#navCategory').click(function(){
		window.location.href = '/m/product/sort?uid='+uid;
	});
	
	$('#navOrders').click(function(){
		window.location.href = '/m/trade/orders?uid='+uid;
	});
	
	$('#navQA').click(function(){
		window.location.href = '/m/inquiry/question?uid='+uid;
	});
	
	$('#headerGoBack').click(function(){
		window.location.href = history.go(-1);
	});
});
