(function(){
	var hm=document.createElement("script");
	hm.src="//hm.baidu.com/hm.js?cc4ad13b29bec016c20ddb20864e1092";
	var s=document.getElementsByTagName("script")[0];
	s.parentNode.insertBefore(hm,s);
})();

var bdInterval;
$().ready(function(){
	$('body').scrollTop(0);
	bdInterval = setInterval("removeBaiudTop()",100);
});

function removeBaiudTop(){
	if($("#hm_t_undefined").html()){
		$("#hm_t_undefined").remove();
		clearInterval(bdInterval);
    }
}

$(window).scroll(function(){
	if($('#scrollTop')){
		if($(window).scrollTop() <= 10){
			$('#scrollTop').hide();
		}else{
			$('#scrollTop').show();
		}
	}
});

$("#scrollTop").click(function(){  
    $('body,html').animate({scrollTop:0},500);  
    return false;
});
