$(window).scroll(function(){
	if($('#scrollTop')){
		if($(window).scrollTop() <= 10){
			$('#scrollTop').hide();
		}else{
			$('#scrollTop').show();
		}
	}
});

function showNav(){
    document.getElementById("nav").className="nav_show";
    stopBubble(event);
    document.onclick = function(){
        document.getElementById("nav").className="hidden";
        document.onclick=null;
    };
}
//阻止冒泡函数
function stopBubble(e){
    if(e && e.stopPropagation){
        e.stopPropagation();    //w3c
    }else{
        window.event.cancelBubble=true; //IE
    }
}


$("#pinlei").val("");

$("#pinlei").change(function(){
    $.ajax({
        type : 'POST'//请求方式
        ,url : "/offlinem/brands/indexs"  //请求路径
        ,data : {provinceid:$('#pinlei').val()}  //发送到服务器的数据
        ,cache : false //设置为 false 将不会从浏览器缓存中加载请求信息
        ,async : true //同步请求
        ,timeout :60000//默认超时60秒
        ,dataType:'json' //预期服务器返回的数据类型
        ,success : function(data){
            $("#city").html("<option value=''>-请选择品牌-</option>");
            for(i in data){
                $("#city").append("<option value='"+data[i]['PNAME']+"'>"+data[i]['PNAME']+"</option>");
            }
        }
    });
});

$("#city").change(function(){
    $.ajax({
        type : 'GET'//请求方式
        ,url : "/offlinem/brands/indexss"  //请求路径
        ,data : {province:$('#city').val(),provinceid:$('#pinlei').val()}  //发送到服务器的数据
        ,cache : false //设置为 false 将不会从浏览器缓存中加载请求信息
        ,async : true //同步请求
        ,timeout :60000//默认超时60秒
        ,dataType:'json' //预期服务器返回的数据类型
        ,success : function(data){
            $("#pinpai").html("<option value=''>-请选择品类-</option>");
            for(i in data){
                $("#pinpai").append("<option value='"+data[i]['SSLXID']+"'>"+data[i]['MERNAME']+"</option>");
            }
        }
    });
});




