$(window).scroll(function(){
	if($('#scrollTop')){
		if($(window).scrollTop() <= 10){
			$('#scrollTop').hide();
		}else{
			$('#scrollTop').show();
		}
	}
});

$("#province").val("");

$("#province").change(function(){
    $.ajax({
        type : 'POST'//请求方式
        ,url : "/offlinem/fenxiang/getcitybyprovinceid"  //请求路径
        ,data : {provinceid:$('#province').val()}  //发送到服务器的数据
        ,cache : false //设置为 false 将不会从浏览器缓存中加载请求信息
        ,async : true //同步请求
        ,timeout :60000//默认超时60秒
        ,dataType:'json' //预期服务器返回的数据类型
        ,success : function(data){
            $("#city").html("<option value=''>-请选择城市-</option>");
            for(i in data){
                $("#city").append("<option value='"+data[i]['AREA_NAME']+"'>"+data[i]['AREA_NAME']+"</option>");
            }
        }
    });
});

function showNav(){
    document.getElementById("nav").className="nav_show";
    stopBubble(event);
    document.onclick = function(){
        document.getElementById("nav").className="hidden";
        document.onclick=null;
    };
}
//阻止冒泡函数
function stopBubble(e){
    if(e && e.stopPropagation){
        e.stopPropagation();    //w3c
    }else{
        window.event.cancelBubble=true; //IE
    }
}

$('.footer .api_bottom').click(function(){
    if(!$(this).hasClass('active')){
        var _thisHref = $(this).attr('apihref');
        window.location.href = _thisHref;
    }
})
