var unLoading = true;
function scrollEvent(){
	// 当滚动到最底部以上10像素时， 加载新内容
	var height = $(document).scrollTop() + $(window).height()-$(document).height();
	if (height > -10 && unLoading){
		unLoading = false;
		$(window).unbind('scroll');
		$('#orderlist').append('<div id="loading" style="display:none;" class="item ydm-tc">正在加载中，请稍后...</div>');
		$('#loading').fadeIn('slow', function(){
			var pageNo = parseInt($('#pageNo').val(), 10)+1;
			var totalPage = parseInt($('#totalPage').val(), 10);
			
			if(pageNo > totalPage){
				$('#loading').fadeOut('slow', function(){
					$('#loading').remove();
				});
			}else{
				$('#pageNo').val(pageNo);
				var html = '';
				var i = 0;
				var partnerCode = $('#partnerCode').val();
				var pageNo = $('#pageNo').val();
				var promoCode = $('#promoCode').val();
				$.ajax({
					type:'GET',
					dataType:'json',
					cache:false,
					async:false,
					url:'/offlinem/trade/jsonorders',
					data:{partnerCode:partnerCode, promoCode:promoCode, pageNo:pageNo},
					success:function(data){
						var list = data['result'];
						for(var i = 0; i < list.length; i++){
							html +='<li class="li" orderNo = "'+list[i].ORDERNO+'">';
								html +='<div class="oitop">';
									html +='<span class="tradetype">订单编号：'+list[i].ORDERNO+'</span>';
									html +='<span class="orderstatus">'+list[i].STATUSNAME+'</span>';
								html +=' </div>';
								html +='<div class="oibody"> ';
									html +='<div class="img"><img src=" '+list[i].MERIMG+' "/></div>';
									html +='<div class="mername">';
										html +='<div class="name">'+list[i].SPNAME+'</div>';
										html +='<div class="time">'+list[i].ORDERDATE+'</div>';
										html +='<div class="orice">回收金额：￥'+list[i].ORDERPRICE+'</div>';
										if(list[i].CREDITFLAG && list[i].CREDITMONEY > 0){
											html +='<div class="orice">先行支付金额：￥'+list[i].CREDITMONEY+'</div>';	
										}
									html +='</div>';
								html +='</div>';
							html +='</li>';
						}
						$("#loading").fadeOut("slow", function(){
							$('#loading').remove();
							$('#orderlist').append(html);
						});
						setTimeout(function(){$(window).bind('scroll', function(){scrollEvent();unLoading = true;});}, 1000);
					}
				});
			}
		});
	}
}




var t1 = null;
function loaded(){
//	var ordersScroll = new IScroll('#ordersWrapper',{mouseWheel:false,click:true});
	$("#leftGoBack").on("click",function(e){
		e.preventDefault();
		if (t1 == null){
			t1 = new Date().getTime();
		}else{
			var t2 = new Date().getTime();
			if(t2 - t1 < 500){
				t1 = t2;
				return;
			}else{
				t1 = t2;
			}
		}
		window.history.back();
	});
}
