var int;
$().ready(function(){
	$("#headerGoBack").on("click",function(){window.history.back();});
	$('body').scrollTop(0);
	int = setInterval("removeBaiudTop()",100);
	$("#scrollTop").click(function(){  
	    $('body,html').animate({scrollTop:0},500);  
	    return false;
	});
	
	//回到首页
	$('#goHome, #homeMenu').click(function(){
		top.location.href = '/partner/center';
	});
	
	//登录退出
	$('#loginout').click(function(){
		layer.open({
		    content: '<ul class="alter"><li class="alttop">提示</li><li class="altbody">是否确定退出？</li><ul>',
		    btn: ['确定','取消'],
		    shadeClose:false,
		    yes: function(index){
		    	top.location.href = '/partner/login/loginout';
			},no:function(){
				layer.closeAll();
		    }
		});
	});
});

function removeBaiudTop(){
	if($("#hm_t_undefined").html()){
		$("#hm_t_undefined").remove();
		clearInterval(int);
    }
}

