$().ready(function(){
    $('#btnSubmit').bind('click', function(){formSubmit();});
    $('#teardown .card').click(function(){
        $('#teardown .card').removeClass('selected');
        $(this).addClass('selected');
        $('#teardown_text').html($(this).attr('title'));
    });
    $('#intact .card').click(function(){
        $(this).toggleClass('selected');
        var texts = [];
        $('#intact .card').each(function(){
            if($(this).hasClass('selected')){
                var text = $(this).attr('title');
                texts.push(text);
            }
        });
        if(texts.length > 0 ){
            $('#intact_text').html(texts.join('、'));
        }else{
            $('#intact_text').html('无');
        }
    });
});

function formSubmit(){
    $('#btnSubmit').unbind('click');
    var cIds = [];
    var canSubmit = true;
    var teardownVal = '';
    
    $('#teardown .card').each(function(){
        if($(this).hasClass('selected')){
          teardownVal = $(this).attr('id');
        }
    });
    $('#intact .card').each(function(){
        if($(this).hasClass('selected')){
          cIds.push($(this).attr('id'));
        }
    });
    $('#radioIds').val(teardownVal);
    $('#multiIds').val(cIds.join('#'));
    if(teardownVal != ''){
      $('#inquiryForm').submit();
    }else{
        layer.open({content:'<div class="tiptext">请完整选择商品描述</div>'});
      $('#btnSubmit').bind('click' ,function(){formSubmit();});
    }
}
//重置
function acReset(){window.location.reload();}