var t1 = null;
function loaded(){
//	var ordersScroll = new IScroll('#ordersWrapper',{mouseWheel:false,click:true});
	$("#leftGoBack").on("click",function(e){
		e.preventDefault();
		if (t1 == null){
			t1 = new Date().getTime();
		}else{
			var t2 = new Date().getTime();
			if(t2 - t1 < 500){
				t1 = t2;
				return;
			}else{
				t1 = t2;
			}
		}
		window.history.back();
	});
}

