function writeExpress(orderNo, e){
	if(e && e.stopPropagation){//非IE
        e.stopPropagation();
    }else {//IE
        window.event.cancelBubble = true;
    }
	if (t1 == null){
		t1 = new Date().getTime();
	}else{
		var t2 = new Date().getTime();
		if(t2 - t1 < 500){
			t1 = t2;
			return;
		}else{
			t1 = t2;
		}
	}
    var src = '/offlinem/trade/writeexpress?orderNo='+orderNo;
    window.location.href = src;
}

function stopOrder(orderNo, e){
	if(e && e.stopPropagation){//非IE
        e.stopPropagation();
    }else {//IE
        window.event.cancelBubble = true;
    }
	if (t1 == null){
		t1 = new Date().getTime();
	}else{
		var t2 = new Date().getTime();
		if(t2 - t1 < 500){
			t1 = t2;
			return;
		}else{
			t1 = t2;
		}
	}
	layer.open({
		content: '<ul class="alter"><li class="alttop">提示</li><li class="altbody">是否确定取消订单？</li><ul>',
		shadeClose:false,
		btn: ['确定','取消'],
		yes: function(index){
			$.post('/offlinem/trade/stoporder',{orderNo:orderNo},function(data){
				if(data == 'Y'){
					layer.open({content:'<div class="tiptext">订单取消成功</div>',time:3});
					window.location.href = window.location.href;
				}else{
					layer.open({content:'<div class="tiptext">订单取消失败：'+data+'</div>',time:3});
				}
			});
    	},no:function(){
    		layer.closeAll();
    	}
	});
}


