$().ready(function(){
	$('#keywords').autocomplete({serviceUrl:'/offlinem/product/keywords', params:{pcode:params.pcode, merType:params.merType}, dataType:'json', width:'100%'});
	$('#moreMerTypes li[data-id="'+params.merType+'"]').addClass('hide');
});
$('#moreMerTypes li').click(function(){
	params.merType = $(this).attr('data-id');
	$('#searchTypeText').html($(this).html()+'<i></i>');
	$('#moreMerTypes li').removeClass('hide');
	$(this).addClass('hide');
	$('#moreTypeWrap').toggle();
	$('#keywords').autocomplete({serviceUrl:'/offlinem/product/keywords',
		params:{pcode:params.pcode, merType:params.merType}, dataType:'json', width:'100%'});
});
$('#searchTypeText').click(function(){ $('#moreTypeWrap').toggle();});
$('#keywords').click(function(){
	$('#moreTypeWrap').hide();
});
//文本框查询
function doSearch(){
	var value = $('#keywords').val();
	if(value == ''){
		return;
	}
	location.href = '/offlinem/product/search?keywords='+value+'&mertype='+params.merType+'&pcode='+params.pcode;
}

function merInquiry(merId){
	window.location.href = '/offlinem/inquiry/init?spid='+merId;
}