
function writeExpress(orderNo){
  var src = '/m/trade/writeexpress?orderNo='+orderNo;
  window.location.href = src;
}

function expressTrace(type, number){
	window.location.href = '/m/trade/expresstrade?type='+type+'&number='+number;
}

