$('#tabs li').click(function(){
    $('#tabs li').removeClass('selected');
    $(this).addClass('selected');
    var cid = $(this).attr('cid');
    $('.tabContent').hide();
    $('#tabs_'+cid).show();
});

$().ready(function(){
    var h1 = $('#tabs_1').height();
    var h2 = $('#tabs_2').height();
    h1 = h1 > h2 ? h1 : h2;
    $('.tabContent').height(h1);
});