    var second = $("#second").val();
    var s = second%60;
    var m = parseInt(second/60);
    $(document).ready(function(){
        time();
    });
    function time(){
        if(s >= 0){
            if(s <=9){
                s = '0'+s;
            }
            $('#time').html(m+'分'+s+'秒');
            if( m <= -1){
                $('.downTime').html('支付超时，请重新下单');
                $('.order_bottom .order_cancel').hide();
                $('.order_bottom a').attr('href','/m/product');
                $('.order_gopay').addClass('bg_dark').html('重新下单');
                clearInterval(a);
            }
            s--;
        }else if(s<=0){
            s= 59;
            m--;
            $('#time').html(m+'分'+s+'秒');
            s--;
        }
    }
    a = setInterval('time()',1000);

function orderInfo(orderNo){
	window.location.href = '/zzy/trade/order?orderNo='+orderNo;
}
    
function cancelOrder(orderNo){
	layer.open({
		content:'<ul class="alter"><li class="alttop">提示</li><li class="altbody">是否确定取消订单？</li><ul>',
		shadeClose:false,
		btn:['确定','取消'],
		yes:function(index){
			$.post('/zzy/trade/stoporder',{orderNo:orderNo},function(data){
				if(data == 'Y'){
					layer.open({content:'<div class="tiptext">订单取消成功</div>',time:3});
					window.location.href = window.location.href;
				}else{
					layer.open({content:'<div class="tiptext">订单取消失败：'+data+'</div>',time:3});
				}
			});
		},no:function(){
			layer.closeAll();
		}
	});
}
